mapper.insert:
insert into tbl_member
(
	me_email
,	me_pwd
,	me_name
,	me_real_name
,   me_phone
,   me_regdate
)
values
(
	#{me_email}
,	#{me_pwd}
,	#{me_name}
,	#{me_real_name}
,	#{me_phone}
,	now()
)

mapper.update:
	update tbl_member set
		me_image = #{me_image}
 	where me_id = #{me_id}

mapper.select:
select 
   *
from
tbl_member
where me_email = #{me_email} 
and me_pwd = #{me_pwd}

mapper.update:
