mapper.insert:
insert into tbl_member
(
	me_email
,	me_pwd
,	me_name
,   me_regdate
)
values
(
	#{me_email}
,	#{me_pwd}
,	#{me_name}
,	current_timestamp()
)

mapper.select:
select 
   *
from
tbl_member
where me_email = #{me_email} 
and me_pwd = #{me_pwd}

mapper.update:
	update tbl_member
	set session_key = #{sessionId},
	session_limit = #{sessionLimit}
	where me_id = #{me_id}
